script:
frappe.ui.form.on('Material Request', {
 before_save: function(frm,cdt,cdn){
    cur_frm.set_value("is_fixed_asset", 1);
	cur_frm.refresh_field();
 } 
 })

frappe.ui.form.on('Material Request Item', {
 item_code: function(frm,cdt,cdn){
        var items = locals[cdt][cdn];
        if(items.warehouse!= null && items.warehouse!= undefined){
        frappe.db.get_value("Bin", {"warehouse":items.warehouse,"item_code":items.item_code},["actual_qty"],(w)=>{
            items.stock_available=w.actual_qty;
            refresh_field("items");

            });
        }
    }
})
